import { ComponentFixture, TestBed } from '@angular/core/testing';
import { ReactiveFormsModule, FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { RouterTestingModule } from '@angular/router/testing';
import { HeroesFacade } from '../heroes-page/heroes-facade/heroes.facade';
import { HeroFormComponent } from './hero-form.component';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';

describe('HeroFormComponent', () => {
  let component: HeroFormComponent;
  let fixture: ComponentFixture<HeroFormComponent>;
  let router: Router;
  let heroesFacade: jasmine.SpyObj<HeroesFacade>;

  beforeEach(async () => {
    const heroesFacadeSpy = jasmine.createSpyObj('HeroesFacade', [
      'addNewHero',
      'editHero',
    ]);

    await TestBed.configureTestingModule({
      declarations: [HeroFormComponent],
      imports: [
        ReactiveFormsModule,
        RouterTestingModule,
        MatFormFieldModule,
        MatInputModule,
        BrowserAnimationsModule,
      ],
      providers: [
        FormBuilder,
        { provide: HeroesFacade, useValue: heroesFacadeSpy },
        {
          provide: ActivatedRoute,
          useValue: { snapshot: { paramMap: { get: () => 'testHero' } } },
        },
      ],
      //schemas: [CUSTOM_ELEMENTS_SCHEMA]
    }).compileComponents();

    fixture = TestBed.createComponent(HeroFormComponent);
    component = fixture.componentInstance;
    router = TestBed.inject(Router);
    heroesFacade = TestBed.inject(HeroesFacade) as jasmine.SpyObj<HeroesFacade>;
    spyOn(router, 'navigate');
    fixture.detectChanges();
  });

  it('should validate that name, power, and universe fields are required', () => {
    component.heroForm.controls['name'].setValue('');
    component.heroForm.controls['power'].setValue('');
    component.heroForm.controls['universe'].setValue('');
    expect(component.heroForm.valid).toBeFalsy();

    component.heroForm.controls['name'].setValue('Batman');
    component.heroForm.controls['power'].setValue('Rich');
    component.heroForm.controls['universe'].setValue('DC');
    expect(component.heroForm.valid).toBeTruthy();
  });

  describe('saveHero', () => {
    it('should call addNewHero on submit in create mode', () => {
      component.isEditMode = false;
      component.saveHero();
      expect(heroesFacade.addNewHero).toHaveBeenCalled();
    });

    it('should call editHero on submit in edit mode', () => {
      component.isEditMode = true;
      component.currentHero.name = 'Batman';
      component.saveHero();
      expect(heroesFacade.editHero).toHaveBeenCalledWith(
        'Batman',
        component.heroForm.value
      );
    });
  });

  it('should navigate to "/heroes" after saving', () => {
    component.saveHero();
    expect(router.navigate).toHaveBeenCalledWith(['/heroes']);
  });
});
